questionvariables.text:
ta:true;
points_match(pointA,pointB) := block(
 match:false,
 if (pointA[1] = pointB[1])  and (pointA[2] = pointB[2]) then
 match:true,
return(match)
);
has_right_angles(pointA,pointB) := block(
rangles:false,
if(pointA[1] = 2) or (pointB[1] = 8) then
rangles:true,
return(rangles)
);
/* Define the function to calculate the area of a polygon */
get_area(coordinates) := block(
    [n : length(coordinates), area : 0],
    if n < 3 then return(0), /* Not a polygon if less than 3 points */
    for i : 1 thru n do (
        /* Extract current and the next point */
        x1 : coordinates[i][1],
        y1 : coordinates[i][2],
        x2 : coordinates[mod(i, n) + 1][1],
        y2 : coordinates[mod(i, n) + 1][2],
        /* Apply Shoelace formula */
        area : area + x1*y2 - y1*x2
    ),
    return(float(abs(area) / 2))
);

feedbackvariables.text:
feedback:"";
closed_polygon:points_match(pointD,[8,9]);
if not (closed_polygon) then 
feedback:sconcat(feedback,"That is not a paralellogram the lines to not join. ");

right_angles:has_right_angles(pointB,pointC); 

if(right_angles) then 
feedback:sconcat(feedback,"The question requests no right angles. ");
/**coordinates:[[2,9],[2,8],[8,8],[8,9]];**/
coordinates:[pointA,pointB,pointC,pointD];


area:get_area(coordinates);
if(area = 24.0) then 
feedback: sconcat(feedback,"That shape has the requested area of 24cm<sup>2</sup>. ") 
else 
feedback: sconcat(feedback, "That shape does not have the requested area of 24cm<sup>2</sup>. ")
iscorrect:false;
if(area = 24.0 and not right_angles and closed_polygon) then 
block (
iscorrect:true
)
